mapper.select:
select

	${item}

 from ${tableName}


		where date_format(${standardDate}, '%Y-%m-%d') >= #{fromDate} and 
			  date_format(${standardDate}, '%Y-%m-%d') <= #{toDate} 




limit ${startNum}, ${cntByPage}

select  tableName, date_format(InsertTime, '%Y-%m-%d %H:%i:%s') as InsertTime from uploadlog
order by InsertTime ${order}
limit ${startNum}, ${cntByPage}

   		select count(*) from uploadlog 



	   				where tableName=#{table}


	   				where date_format(InsertTime, '%Y-%m-%d')=#{searchDate}


	   				where tableName=#{table} and date_format(InsertTime, '%Y-%m-%d')=#{searchDate}

select count(*) from ${table}


		where date_format(${standardDate}, '%Y-%m-%d') >= #{fromDate} and 
			  date_format(${standardDate}, '%Y-%m-%d') <= #{toDate}

